mapper.update:
UPDATE power_activity


        name = #{update.name},


        desc = #{update.desc},


        url = #{update.url},


        imageUrl = #{update.imageUrl},


        type = #{update.type},


        bonuses = #{update.bonuses},


        content = #{update.content},


        startTime = #{update.startTime},


        endTime = #{update.endTime},





    AND id = #{condition.id}


    AND name = #{condition.name}


    AND desc = #{condition.desc}


    AND url = #{condition.url}


    AND imageUrl = #{condition.imageUrl}


    AND type = #{condition.type}


    AND bonuses = #{condition.bonuses}


    AND content = #{condition.content}


    AND startTime = #{condition.startTime}


    AND endTime = #{condition.endTime}

UPDATE power_activity
name = #{name}, desc = #{desc}, url = #{url}, imageUrl = #{imageUrl}, type = #{type}, bonuses = #{bonuses}, content = #{content}, startTime = #{startTime}, endTime = #{endTime},
WHERE id = #{id}

mapper.select:
SELECT



            , id


            , name


            , desc


            , url


            , imageUrl


            , type


            , bonuses


            , content


            , startTime


            , endTime







        FROM power_activity




        AND   id  ${condition.id.op} #{condition.id.data}


        AND id like CONCAT('%', #{condition.id.data} , '%')


        AND id like CONCAT(#{condition.id.data} , '%')


        ${condition.groupOp}   id  ${condition.id.op}
        #{listItem}





        AND   name  ${condition.name.op} #{condition.name.data}


        AND name like CONCAT('%', #{condition.name.data} , '%')


        AND name like CONCAT(#{condition.name.data} , '%')


        ${condition.groupOp}   name  ${condition.name.op}
        #{listItem}





        AND   desc  ${condition.desc.op} #{condition.desc.data}


        AND desc like CONCAT('%', #{condition.desc.data} , '%')


        AND desc like CONCAT(#{condition.desc.data} , '%')


        ${condition.groupOp}   desc  ${condition.desc.op}
        #{listItem}





        AND   url  ${condition.url.op} #{condition.url.data}


        AND url like CONCAT('%', #{condition.url.data} , '%')


        AND url like CONCAT(#{condition.url.data} , '%')


        ${condition.groupOp}   url  ${condition.url.op}
        #{listItem}





        AND   imageUrl  ${condition.imageUrl.op} #{condition.imageUrl.data}


        AND imageUrl like CONCAT('%', #{condition.imageUrl.data} , '%')


        AND imageUrl like CONCAT(#{condition.imageUrl.data} , '%')


        ${condition.groupOp}   imageUrl  ${condition.imageUrl.op}
        #{listItem}





        AND   type  ${condition.type.op} #{condition.type.data}


        AND type like CONCAT('%', #{condition.type.data} , '%')


        AND type like CONCAT(#{condition.type.data} , '%')


        ${condition.groupOp}   type  ${condition.type.op}
        #{listItem}





        AND   bonuses  ${condition.bonuses.op} #{condition.bonuses.data}


        AND bonuses like CONCAT('%', #{condition.bonuses.data} , '%')


        AND bonuses like CONCAT(#{condition.bonuses.data} , '%')


        ${condition.groupOp}   bonuses  ${condition.bonuses.op}
        #{listItem}





        AND   content  ${condition.content.op} #{condition.content.data}


        AND content like CONCAT('%', #{condition.content.data} , '%')


        AND content like CONCAT(#{condition.content.data} , '%')


        ${condition.groupOp}   content  ${condition.content.op}
        #{listItem}





        AND   startTime  ${condition.startTime.op} #{condition.startTime.data}


        AND startTime like CONCAT('%', #{condition.startTime.data} , '%')


        AND startTime like CONCAT(#{condition.startTime.data} , '%')


        ${condition.groupOp}   startTime  ${condition.startTime.op}
        #{listItem}





        AND   endTime  ${condition.endTime.op} #{condition.endTime.data}


        AND endTime like CONCAT('%', #{condition.endTime.data} , '%')


        AND endTime like CONCAT(#{condition.endTime.data} , '%')


        ${condition.groupOp}   endTime  ${condition.endTime.op}
        #{listItem}





ORDER BY

    ${sort.field} ${sort.order}

LIMIT 0,1

